// 创建路由模块
// 导入Vue
import Vue from "vue";
// 导入Vue-route
import VueRouter from "vue-router";
//  创建Vue插件
Vue.use(VueRouter);
// 导入路由
import Login from "@/components/LoginV.vue";
// import Home from "@/components/HeadV.vue"

export default new VueRouter({
  routes: [
    { path: "/login", component: Login },
    // {path:'/home',component: Home},
    { path: "/", redirect: "/login" },
  ],
});
